import React from 'react'
import './calculator.css';
import Algebrite from 'algebrite'



class Calculator extends React.Component {
  constructor(props) {
    super(props)
    // this.state = { equation: '' }
    this.updateEquation = this.updateEquation.bind(this)
  }

  render() {
    return (
      <div>
        <div id='result'>No Equation</div>
        <input value='x' className='equations' id='equation'/>
        <div>
          <input placeholder={'Lower Limit'} className='equation' />
          <input placeholder={'Upper Limit'} className='equation' />
        </div>
        <button onClick={this.updateEquation}>Update</button>
      </div>
    )
  }

  updateEquation() {
    let equation = document.getElementById('equation').value
    let resultElement = document.getElementById('result')

    if (!equation) {
      resultElement.innerHTML = 'No Equation'
    } else {
      resultElement.innerHTML = Algebrite.defint(Algebrite.run(equation), 'x', 0, 10)
    }
  }





}

export default Calculator;
